catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Gets object from field and passes in callback
    g_form.getReference('hire_group', setOptions);

    function setOptions(sG) {
        var selectedGrp = sG.name.toLowerCase();

        //Defines options
        var laptop = {display: 'PC Laptop', value: 'pc_laptop'};
        var mackbook = {display: 'MacBook', value: 'mackbook'};
        var desktop = {display: 'Desktop', value: 'desktop'};
        var imac = {display: 'iMac', value: 'imac'};
        var thinClient = {display: 'Thin Client', value: 'thin_client'};
		var notApplicable = {display: 'Not Applicable', value: 'not_applicable'};

        // Builds an object of group and associated computer selection
        var compbyGrp = {
            'accounting': {dflt: laptop},
            'finance': {dflt: laptop},
            'fraud': {dflt: laptop},
            'finance operations': {dflt: laptop},
            'human resources': {dflt: laptop},
            'office support': {dflt: laptop},
            'recruiting': {dflt: laptop},
            'integrated marketing': {dflt: laptop},
            'customer care management': {dflt: laptop},
            'customer care scheduling/reporting': {dflt: laptop},
            'customer care training': {dflt: laptop},
            'buying': {dflt: laptop},
            'planning%20%26%20site%20merchandising': {dflt: laptop},
            'merchandising': {dflt: laptop},
            'marketing': {dflt: mackbook},
            'product management':  {dflt: mackbook},
            'data analyst': {dflt: desktop},
            'copy writer': {dflt: desktop},
            'coordinator': {dflt: desktop},
            'customer care representative': {dflt: thinClient},
            'photo studio digital imaging': {dflt: imac},
            'picking/putaway': {dflt: notApplicable},
            'receiving department': {dflt: notApplicable},
            'returns department': {dflt: notApplicable},
            'shipping department': {dflt: notApplicable},
            'customer care special team': {dflt: laptop, alt: thinClient},
            'creative % 28graphics% 29': {dflt: mackbook, alt: imac},
            'engineering': {dflt: mackbook, alt: laptop},
            'data services': {dflt: mackbook, alt: laptop},
            'technology operations': {dflt: mackbook, alt: laptop},
            'itsm': {dflt: laptop, alt: mackbook},
            'enterprise support': {dflt: laptop, alt: mackbook},	
        };
        //checks object for property and sets values based on results
        if (compbyGrp.hasOwnProperty(selectedGrp)) {
            var dfltDisplay = compbyGrp[selectedGrp].dflt.display;
            var dfltValue = compbyGrp[selectedGrp].dflt.value;
            if (!compbyGrp[selectedGrp].hasOwnProperty('alt')) {
                g_form.clearOptions('computer_choice');
                g_form.addOption('computer_choice', dfltValue, dfltDisplay);
            } else {
                var altDisplay = compbyGrp[selectedGrp].alt.display;
                var altValue = compbyGrp[selectedGrp].alt.value;
                g_form.clearOptions('computer_choice');
                g_form.addOption('computer_choice', dfltValue, dfltDisplay);
                g_form.addOption('computer_choice', altValue, altDisplay);
            }
        }
    }
}
